for i in $1/*.d ; do echo $i ; sed "s|RAWDIR|$i|" fp.manifest.txt > man.1.txt ;  j=$(basename $i) ; k=${j%%.*} ; echo $k ; sed "s|RAWFILE|$k|" man.1.txt > man.2.txt ; $HOME/fragpipe/bin/fragpipe --headless --threads 20 --ram 80 --workflow fp.workflow.txt --manifest man.2.txt --workdir $j ; cd $j ; echo $PWD; $HOME/.local/bin/AA_stat --pepxml $k/$k.pepXML ; cd .. ; echo $PWD ; done
