from pyteomics import fasta, parser
import sys
fastaF = sys.argv[1]
#fastaF = "L:\promec\FastaDB\uniprot-salmon-combo-iso-Apr23.fasta"
peptide = sys.argv[2]
#peptide = ""
peptide = peptide.upper()
peptide = peptide.replace("L", "I") 
import time
chkPoint=time.strftime("%H.%M.%S.%d.%m.%Y")
fastaFO=fastaF+chkPoint+".hits.csv"#rem peptide from name
print('Searching ', fastaF ,'for peptide',peptide,'and writing to... \n',fastaFO,'...\n')
f= open(fastaFO,"w+")
#https://stackoverflow.com/questions/3437059/does-python-have-a-string-contains-substring-method
for description, sequence in fasta.FASTA(fastaF):
  sequence = sequence.upper()
  sequence = sequence.replace("L", "I")
  #print(description, sequence, peptide)
  if peptide in sequence: 
    f.write(description)
    words = description.split('=') 
    words = ','.join(words)
    print(words)
    f.write(str(words))
    f.write('\n')
f.close()
